(2906, 2663, 'CENTRE_IMAGE', 'http://files.spip.org/spip-zone/centre_image.png', '000.008.002', '', '[3.0.19;3.1.*]', '3.0,3.1', '<multi>\n[fr]Permet de calculer automatiquement un centre d''intérêt d''une image ou de l''indiquer manuellement.\n</multi>', 'a:1:{i:1;a:3:{s:3:"nom";s:5:"ARNO*";s:3:"url";s:0:"";s:4:"mail";s:0:"";}}', '', 'a:1:{i:1;a:2:{s:3:"nom";s:3:"GPL";s:3:"url";s:40:"http://www.gnu.org/licenses/gpl-3.0.html";}}', '', 'http://seenthis.net/messages/362099', '', '', 'dev', 2, 'a:3:{s:9:"necessite";a:1:{i:0;a:1:{s:6:"medias";a:2:{s:3:"nom";s:6:"medias";s:13:"compatibilite";s:9:"[2.7.64;[";}}}s:9:"librairie";a:0:{}s:7:"utilise";a:0:{}}', '2016-02-20 19:45:52', '2016-02-20 19:45:52', 1, 'centre_image.zip', 184946, '2016-02-20 20:04:02', '_plugins_/centre_image/trunk', 'a:1:{s:12:"centre_image";a:2:{s:9:"reference";s:2:"fr";s:12:"gestionnaire";s:0:"";}}', 'non', 'non', 0, '', 'non', 'non', 'non', '', '8531dae905599db75786dcd26dbb6dfe'),
